document.querySelector("form").addEventListener("submit",(function(e){e.preventDefault();const t=document.getElementById("set-username").value,n=document.getElementById("set-username").value;fetch("/settings/account",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:t,email:n})}).then((e=>e.json())).then((e=>{e.success?alert("Account details updated"):alert("Error updating account")})).catch((e=>{console.log(e.message)}))}));